mapper.select:
select h.id,type_id typeId,title,description,price,pubdate,
floorage,contact,h.street_id streetId,path,district_id districtid from house h left join street s on
h.street_id=s.id where h.id=#{id}

select house.id,type_id typeId,title,price,pubdate,floorage,contact,street_id streeId,path,
type.name tname,
street.name sname,
district.name dname
from house
left join type on house.TYPE_ID=type .ID
left join street on house.STREET_ID=street.ID
left join district on street.DISTRICT_ID=district.ID
where ispass=1 and isdel=0

  and title like concat('%',#{title},'%')


  and district_id=#{did}


  and street_id=#{sid}


  and type_id=#{tid}


and price > #{startPrice}


  and price < #{endPrice}
